import React, { PureComponent } from 'react'
import { Button, Table } from 'antd'
import 'antd/dist/antd.min.css'
import './UserList.css'
import AddUserModal from './AddUserModal'
export default class UserList extends PureComponent {
    constructor(){
        super()
        this.state={
            isDrawerOpen:false,
            users:[]
        }
    }


    columns=[
        {
            title: 'First Name',
            dataIndex: 'firstName',
            key: 'firstName',
          },
          {
            title: 'Last Name',
            dataIndex: 'lastName',
            key: 'lastName',
          },
          {
            title: 'Email',
            dataIndex: 'email',
            key: 'email',
          },
          {
            title: 'Password',
            dataIndex: 'password',
            key: 'password',
          },
          {
            title: 'User Type',
            dataIndex: 'userType',
            key: 'userType',
          },
    ]

    saveUser=(user)=>{
        this.setState((prevState)=>({
            users:[...prevState.users,user]
        }))
        this.onClose()

    }
    handleAddClick=()=>{
        this.setState({isDrawerOpen:true})
    }
    onClose=()=>{
        this.setState({isDrawerOpen:false})
    }
  render() {
    return (
      <div className='main-container'>
        <Button onClick={this.handleAddClick} className="add-button">Add User</Button>
        <Table dataSource={this.state.users} columns={this.columns} />
        <AddUserModal isOpen={this.state.isDrawerOpen} onClose={this.onClose} saveUser={this.saveUser} ></AddUserModal>
      </div>

    )
  }
}
