import React, { PureComponent } from 'react'
import { Button, Table } from 'antd'
import 'antd/dist/antd.min.css'
import './UserList.css'
import AddUserModal from './AddUserModal'
export default class UserList extends PureComponent {
    constructor(){
        super()
        this.state={
            isDrawerOpen:false
        }
    }
    users=[
        {
            firstName:"Robin",
            lastName:"Singh",
            age:23,
            phone:67384894
        },
        {
            firstName:"Robin",
            lastName:"Singh",
            age:23,
            phone:67384894
        },
    ]

    columns=[
        {
            title: 'FirstName',
            dataIndex: 'firstName',
            key: 'firstName',
          },
          {
            title: 'LastName',
            dataIndex: 'lastName',
            key: 'lastName',
          },
          {
            title: 'Age',
            dataIndex: 'age',
            key: 'age',
          },
          {
            title: 'Phone',
            dataIndex: 'phone',
            key: 'phone',
          },
    ]

    saveUser=(user)=>{
        console.log("%%%%%5",user)
    }
    handleAddClick=()=>{
        this.setState({isDrawerOpen:true})
    }
    onClose=()=>{
        this.setState({isDrawerOpen:false})
    }
  render() {
    return (
      <div className='main-container'>
        <Button onClick={this.handleAddClick} className="add-button">Add User</Button>
        <Table dataSource={this.users} columns={this.columns} />
        <AddUserModal isOpen={this.state.isDrawerOpen} onClose={this.onClose} saveUser={this.saveUser} ></AddUserModal>
      </div>

    )
  }
}
